    Full       : in     std_logic;
    Rst        : in     std_logic;
    Tx_Fire    : in     std_logic;
    UDP_Tx     : out    dbus_type;
    WrReq      : out    std_logic;
    numpackets : in     std_logic_vector(9 downto 0);
