architecture fsm of FirePacketGen is

  -- State Machine Options:
  --  Clock : Clk (Rising edge).
  --  State assignment : Enumerate.
  --  State decoding : Case construct.
  --  Actions on transitions : Clocked.
  --  Actions on states : Clocked.

  type state_type is (Idle, Packet) ;
  signal state : state_type ;  -- Current State


begin

  state_decoding: process (Clk, Rst) is
      Variable Cnt: Integer Range 0 to 16383;
      Variable Frame: Integer Range 0 to 1023;


  begin
    if (Rst = '1') then
      state <= Idle ;
      -- Initalize:
      UDP_Tx.EOD <= '0';
      UDP_Tx.D <= x"aa11";
      WrReq <= '0';
      Cnt := 0;
      Frame := 0;
    elsif (rising_edge(Clk)) then
      lbl_state : case state is
        when Idle =>
          if (Tx_Fire = '1') then
            state <= Packet ;
            Cnt := 0;
            Frame := 1;
            If Full = '0' Then
               Cnt := Cnt + 1;
            End If;
            If Cnt = to_integer(unsigned (numwords)) Then
               UDP_Tx.EOD <= '1';
               UDP_Tx.D <= x"0E0D";
            ElsIf Cnt = 1 Then
               UDP_Tx.EOD <= '0';
               UDP_Tx.D <= x"050D";
            Else
               UDP_Tx.EOD <= '0';
               UDP_Tx.D <= Std_Logic_Vector(To_Unsigned(Cnt,16));
            End If;
            WrReq <= Not Full;
          else
            state <= Idle ;
            UDP_Tx.EOD <= '0';
            UDP_Tx.D <= x"aa11";
            WrReq <= '0';
          end if ;
        when Packet =>
          if (Full = '0' And Cnt=to_integer(unsigned (numwords)) And Frame = to_integer(unsigned (numpackets))) then
            state <= Idle ;
            UDP_Tx.EOD <= '0';
            UDP_Tx.D <= x"aa11";
            WrReq <= '0';
          elsif (
            Full = '0' And Cnt = to_integer(unsigned (numwords))

            ) then
            state <= Packet ;
            Cnt := 0;
            Frame := Frame + 1;
            If Full = '0' Then
               Cnt := Cnt + 1;
            End If;
            If Cnt = to_integer(unsigned (numwords)) Then
               UDP_Tx.EOD <= '1';
               UDP_Tx.D <= x"0E0D";
            ElsIf Cnt = 1 Then
               UDP_Tx.EOD <= '0';
               UDP_Tx.D <= x"050D";
            Else
               UDP_Tx.EOD <= '0';
               UDP_Tx.D <= Std_Logic_Vector(To_Unsigned(Cnt,16));
            End If;
            WrReq <= Not Full;
          else
            state <= Packet ;
            If Full = '0' Then
               Cnt := Cnt + 1;
            End If;
            If Cnt = to_integer(unsigned (numwords)) Then
               UDP_Tx.EOD <= '1';
               UDP_Tx.D <= x"0E0D";
            ElsIf Cnt = 1 Then
               UDP_Tx.EOD <= '0';
               UDP_Tx.D <= x"050D";
            Else
               UDP_Tx.EOD <= '0';
               UDP_Tx.D <= Std_Logic_Vector(To_Unsigned(Cnt,16));
            End If;
            WrReq <= Not Full;
          end if ;
      end case lbl_state ;
    end if ; --  Reset & Clock
  end process state_decoding ;

end architecture fsm ; -- of FirePacketGen

